Rules.InvariantScript:
If(
    varCharIndex >= 0 && varCharIndex <= 7,
    // Validate the char between 0..9
    If (
        htmlCommsImg.Height >= 48 && htmlCommsImg.Height <= 57,
        Set(
            varVersionString,
            varVersionString & Char(htmlCommsImg.Height)
        );
        Set(
            varCharIndex,
            varCharIndex + 1
        )
    );
    Set(
        varCacheInvalidator,
        GUID()
    ),
    // Else stop timer
    Set(
        varTimerRunning,
        false
    );
    'Latest Version Info'.VersionFetched(
        Value(
            Coalesce(
                varVersionString,
                "0"
            )
        )
    )
)

Set(varCharIndex, 0);
Set(varTimerRunning, true);
Set(varVersionString, "");
Set(varCacheInvalidator, GUID());
